import gmpy2
def crt(ct, m):
    N = gmpy2.mul(*m)
    result = 0

    for ci, ni in zip(ct, m):
        Ni = N // ni
        Mi = gmpy2.invert(Ni, ni)
        result += gmpy2.mul(gmpy2.mul(ci, Ni), Mi)

    return gmpy2.f_mod(result, N)
